chart.options:
chart: {
  type: 'bar',
  height: 380
},
xaxis: {
  type: 'category',
  labels: {
    formatter: function(val) {
      return "Q" + dayjs(val).quarter()
    }
  },
  group: {
    style: {
      fontSize: '10px',
      fontWeight: 700
    },
    groups: [
      { title: '2019', cols: 4 },
      { title: '2020', cols: 4 }
    ]
  }
},
title: {
    text: 'Custom with Grouped Labels',
},
subtitle: {
    text: 'X-axis with sub categories',
    align: 'center',
},
tooltip: {
  x: {
    formatter: function(val) {
      return "Q" + dayjs(val).quarter() + " " + dayjs(val).format("YYYY")
    }  
  }
},
