scripts.script:
function update() {
  function randomSeries() {
    var arr = []
    for(var i = 0; i < 6; i++) {
        arr.push(Math.floor(Math.random() * 100)) 
    }

    return arr
  }

  chart.updateSeries([{
    name: 'Series 1',
    data: randomSeries(),
  }, {
    name: 'Series 2',
    data: randomSeries(),
  }, {
    name: 'Series 3',
    data: randomSeries(),
  }])
}

chart.options:
chart: {
  height: 350,
  type: 'radar',
  dropShadow: {
    enabled: true,
    blur: 1,
    left: 1,
    top: 1
  }
},
title: {
  text: 'Radar Chart - Multi Series'
},
stroke: {
  width: 0
},
fill: {
  opacity: 0.4
},
markers: {
  size: 0
},
labels: ['2011', '2012', '2013', '2014', '2015', '2016']
